const landUseEl=document.getElementById("land-use"),patternEls=landUseEl.querySelectorAll(".pattern");for(el of patternEls)el.classList.add("animated");const LandUseElIsInView=()=>{const e=landUseEl.getBoundingClientRect().bottom,t=window.innerHeight;return t>=e},handleScroll=()=>{if(LandUseElIsInView()){for(el of patternEls)el.classList.add("animate");window.removeEventListener("scroll",handleScroll)}};window.addEventListener("scroll",handleScroll)